mapper.select:
SELECT COUNT(bs_tenant_user.id) AS num, DATE_FORMAT(bs_tenant_user.addDate,'%Y-%m-%d') AS label
FROM bs_tenant_user


        bs_tenant_user.tenant_id=#{tenant}


        AND bs_tenant_user.addDate >=  #{beginDate}


        AND bs_tenant_user.addDate <=  #{endDate}


GROUP BY label
ORDER BY label DESC
LIMIT 30

